import {useRecoilState} from "recoil";
import {categoryNavigation} from "../../../GlobalState";


const menuCatArray = [
  {
    category: 1, title: "Textil"
  },
  {
    category: 2, title: "Corte"
  },
  {
    category: 3, title: "Confección"
  }
]


function ButtonsModalCategories(props) {
